    //       type="text"
    //       placeholder="Search by recipe name"
    //       value={recipeInput}
    //       onChange={handleFilter}
    //     />
    //     {recipeInput.length === 0 ? <SearchIcon /> : <CloseIcon />}
    //   </>
  );
};

export default SearchBar;
